import UIKit
import RxSwift

class StoriesInteractor: StoriesInteractorProtocol {

    weak var presenter: StoriesPresenterProtocol?
    
    var api: ApiManager
    
    init() {
        api = ApiManager.api
    }
    
    func getPopularVideos(page: Int) -> Observable<[VideoModel]> {
        return api.getPopularVideos(page: page)
            .map {
                let data = $0.results
                return data
        }
    }
    
    func getTopRatedVideos(page: Int) -> Observable<[VideoModel]> {
        return api.getTopRatedVideos(page: page)
            .map {
                let data = $0.results
                return data
        }
    }
}
